DROP TABLE IF EXISTS job;

CREATE TABLE job(
  id INT UNSIGNED NOT NULL AUTO_INCREMENT,
  jobId CHAR(64),
  description CHAR(255),
  bricklayerRate CHAR(64),
  bricklayerMP CHAR(64),
  bricklayerM1 CHAR(64),
  foremanRate CHAR(64),
  foremanMP CHAR(64),
  laborRate CHAR(64),
  laborMP CHAR(64),
  laborM1 CHAR(64),
  operatorRate CHAR(64),
  operatorMP CHAR(64),
  operatorM1 CHAR(64),
  landscaperRate CHAR(64),
  landscaperMP CHAR(64),
  beneco BOOLEAN,
  perdiem BOOLEAN,
  PRIMARY KEY(id),
  UNIQUE(jobId),
  INDEX(jobId)
);


